import copy
from sklearn.base import clone, BaseEstimator, TransformerMixin

# Cell
class Correlation(BaseEstimator, TransformerMixin):

    def __init__(self, thresh):
        self.thresh = thresh

    def fit(self, df):
        correlation = df.corr()
        var = df.var()
        correlated_features = set()
        for i in range(correlation.shape[0]):
            for j in range(i):
                if abs(correlation.iloc[i, j]) > self.thresh:
                    if var[correlation.columns[i]] >= var[correlation.columns[j]]:
                        colname = correlation.columns[j]
                    else:
                        colname = correlation.columns[i]
                    correlated_features.add(colname)

        self.correlated = correlated_features

    def transform(self, df):
        return df.drop(columns=self.correlated)